CREATE OR REPLACE FUNCTION elimina_acentuacao (ds_campo_p text) RETURNS varchar AS $body$
DECLARE


ds_campo_w    	varchar(4000);
i     		integer;
k     		integer;
ds_acento_w	varchar(255)	:= obter_desc_expressao(1045711);
ds_novo_w	varchar(255)	:= 'aaaaaaeeeeioooouucAAAAAAEEEEIOOOOUUCnN';
ds_car_w	varchar(2);



BEGIN
ds_campo_w		:= '';
if (coalesce(ds_campo_p::text, '') = '') or (ds_campo_p = '') then
	ds_campo_w	:= substr(ds_campo_p,1,4000);
else
	begin
	FOR i IN 1..length(ds_campo_p) LOOP
 		begin
		ds_car_w	:= substr(ds_campo_p, i, 1);
		k			:= position(ds_car_w in ds_acento_w);
		if (k = 0) then
			ds_campo_w	:= substr(ds_campo_w || ds_car_w,1,4000);
		else
			ds_campo_w	:= substr(ds_campo_w || substr(ds_novo_w,k,1),1,4000);
		end if;
		end;
	END LOOP;
	end;
end if;
RETURN ds_Campo_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 IMMUTABLE;
