		/*àáâãäåæ*/

		if (ie_achou_w = 'N') then
			for k in 224..230 loop
				if (substr(ds_campo_p,i,1) = chr(k)) then
					if (coalesce(ie_elimina_acentuacao_p,'N') = 'S') then
						char_w	:=	'a';
