CREATE OR REPLACE FUNCTION eme_obter_dose_conv ( cd_unidade_baixa_p text, cd_material_p bigint, qt_material_p bigint, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE

cd_unidade_medida_w	varchar(30);
qt_material_w		double precision;

BEGIN
select	substr(obter_dados_material_estab(cd_material,cd_estabelecimento_p,'UMS'),1,30) cd_unidade_medida_consumo
into STRICT	cd_unidade_medida_w
from	material
where	cd_material	= cd_material_p;

if (cd_unidade_medida_w <> cd_unidade_baixa_p) then
	qt_material_w := obter_dose_convertida(cd_material_p,qt_material_p,cd_unidade_baixa_p,cd_unidade_medida_w);
else
	qt_material_w := qt_material_p;
end if;



return	qt_material_w;

end;
$body$
