CREATE OR REPLACE FUNCTION eme_obter_motivo_cancelamento (nr_motivo_cancel_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(80);


BEGIN
if (nr_motivo_cancel_p IS NOT NULL AND nr_motivo_cancel_p::text <> '') then
	select	substr(ds_motivo, 1, 80)
	into STRICT	ds_retorno_w
	from	eme_motivo_cancel_contr
	where	nr_sequencia = nr_motivo_cancel_p;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
