CREATE OR REPLACE FUNCTION eme_obter_preco_proced ( cd_estabelecimento_p bigint, nr_atendimento_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_convenio_p bigint, cd_categoria_p bigint) RETURNS bigint AS $body$
DECLARE

 
cd_convenio_w		integer;
cd_categoria_w		varchar(10);
vl_resultado_w		double precision;


BEGIN 
 
select	coalesce(obter_convenio_atendimento(nr_atendimento_p),0) 
into STRICT	cd_convenio_w
;
 
select	coalesce(Obter_Dados_Categ_Conv(nr_atendimento_p,'CA'),0) 
into STRICT	cd_categoria_w
;
 
if (ie_origem_proced_p not in (2,3)) then 
	select	obter_preco_procedimento(cd_estabelecimento_p, 
					CASE WHEN cd_convenio_w=0 THEN cd_convenio_p  ELSE cd_convenio_w END , 
					CASE WHEN cd_categoria_w=0 THEN cd_categoria_p  ELSE cd_categoria_w END , 
					clock_timestamp(), 
					cd_procedimento_p, 
					ie_origem_proced_p,0,0,0,0,0,NULL,NULL,NULL,NULL,'P') 
	into STRICT	vl_resultado_w 
	;
elsif (ie_origem_proced_p	= 2) then 
	select	Obter_Preco_Perm_SUS_AIH(cd_estabelecimento_p,0,cd_procedimento_p,ie_origem_proced_p) 
	into STRICT	vl_resultado_w 
	;
elsif (ie_origem_proced_p	= 3) then 
	select	Obter_preco_Sus_Bpa(cd_estabelecimento_p,0,cd_procedimento_p,ie_origem_proced_p) 
	into STRICT	vl_resultado_w 
	;
end if;
 
RETURN coalesce(vl_resultado_w,0);
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
