CREATE OR REPLACE FUNCTION eme_obter_unidade_medida (cd_material_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


cd_unidade_medida_w	varchar(30);
ie_prioridade_w		smallint;


BEGIN
if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
	select	max(cd_unidade_medida)
	into STRICT	cd_unidade_medida_w
	from	material_conversao_unidade a
	where	cd_material = cd_material_p
	and	ie_prioridade = (SELECT	max(ie_prioridade)
				from	material_conversao_unidade b
				where	a.cd_material = b.cd_material);

	if (coalesce(cd_unidade_medida_w::text, '') = '') then
		select	substr(obter_dados_material_estab(cd_material_p,cd_estabelecimento_p,'UME'),1,30)
		into STRICT	cd_unidade_medida_w
		;
	end if;
end if;

return	cd_unidade_medida_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
