CREATE OR REPLACE FUNCTION eme_obter_valor_fatura (nr_seq_contrato_p bigint, nr_faturamento_p bigint) RETURNS bigint AS $body$
DECLARE

 
ie_base_calculo_w	varchar(1);
vl_resultado_w		double precision;			
			 

BEGIN 
 
select	max(a.ie_base_calculo) 
into STRICT	ie_base_calculo_w 
from	eme_regra_preco a, 
	eme_contrato b 
where	b.nr_seq_regra_preco	= a.nr_sequencia 
and	b.nr_sequencia		= nr_seq_contrato_p;
 
	 
if (ie_base_calculo_w = 'A') then 
	begin 
	select	sum(CASE WHEN coalesce(c.vl_preco,0)=0 THEN coalesce(eme_obter_preco_proced(a.cd_estabelecimento, 					coalesce(e.nr_atendimento,0), 					d.cd_procedimento, 					d.ie_origem_proced, 					coalesce(e.cd_convenio, a.cd_convenio), 					coalesce(e.cd_categoria, a.cd_categoria)),0)  ELSE c.vl_preco END ) 
	into STRICT	vl_resultado_w 
	from	eme_contrato a, 
		eme_preco_adic c, 
		eme_proced_chamado d, 
		eme_chamado e 
	where	a.nr_sequencia	= c.nr_seq_contrato 
	and	a.nr_sequencia	= e.nr_seq_contrato 
	and	e.nr_sequencia	= d.nr_seq_chamado 
	and	a.nr_sequencia	= nr_seq_contrato_p 
	and	c.cd_procedimento = d.cd_procedimento 
	and	c.ie_origem_proced = d.ie_origem_proced 
	and	nr_seq_faturamento = nr_faturamento_p;
	end;
else 
	begin 
	select	sum(c.vl_preco) vl_preco 
	into STRICT	vl_resultado_w 
	from	eme_preco_adic c, 
		eme_contrato a 
	where	a.nr_sequencia	= c.nr_seq_contrato 
	and	a.nr_sequencia	= nr_seq_contrato_p;
	end;
end if;	
 
return	vl_resultado_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
