SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION eme_reg_obter_desc_resposta (nr_seq_eme_regulacao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ds_desc_w		varchar(255);
ds_nr_seq_w		bigint;


BEGIN
if (nr_seq_eme_regulacao_p IS NOT NULL AND nr_seq_eme_regulacao_p::text <> '') then

SELECT 	MAX(d.ds_resposta),
	max(d.nr_sequencia)
into STRICT	ds_desc_w,
	ds_nr_seq_w
FROM	eme_reg_resp d,
        	eme_reg_evolucao e
WHERE	e.nr_seq_resposta = d.nr_sequencia
AND	e.nr_seq_reg = nr_seq_eme_regulacao_p;

end if;

if (ie_opcao_p = 'D') then
	ds_retorno_w := ds_desc_w;
elsif (ie_opcao_p = 'F') then
	ds_retorno_w := ds_nr_seq_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
