CREATE OR REPLACE FUNCTION eme_verifica_hist_proced (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_classe_profissional_w 	varchar(3);
ds_retorno_w				varchar(3) := 'S';


--verifica se o paciente tem procedimento cadastrado nas regras de procedimento
C01 CURSOR FOR
	SELECT 	a.ie_classe_profissional
	from 	EME_REGRA_PROCED a,
			PROCEDIMENTO_PACIENTE b
	where  	1 = 1
	and	   	a.CD_PROCEDIMENTO = b.cd_procedimento
	and	   	a.IE_ORIGEM_PROCED = b.ie_origem_proced
	and	   	a.cd_estabelecimento = obter_estabelecimento_ativo
	and	   	b.nr_atendimento = nr_atendimento_p;


BEGIN

open C01;
loop
fetch C01 into
	ie_classe_profissional_w;
exit when(C01%notfound) or (ds_retorno_w = 'N');
	begin
--verifica se existe hist√≥rico liberado para akele atendimento, por um profissional = ao da regra dacastrada
		select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ds_retorno_w
		from   	evolucao_paciente c,
				usuario g
		where  	c.cd_medico = g.cd_pessoa_fisica
		and	   	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
		and    	c.nr_atendimento = nr_atendimento_p
		and	   	g.ie_profissional = ie_classe_profissional_w;
	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
