SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION est_obter_medico_movto ( nr_movimento_estoque_p bigint) RETURNS varchar AS $body$
DECLARE

 
 
cd_pessoa_fisica_w			varchar(10);
nm_paciente_w				varchar(200);
ie_origem_documento_w		varchar(3);
nr_documento_w			bigint;
nr_seq_docto_w			bigint;
nr_atendimento_w			bigint;

/* Matheus OS 44168 em 15/06/2007 */
 

BEGIN 
 
cd_pessoa_fisica_w			:= '';
nm_paciente_w				:= '';
 
select	coalesce(max(ie_origem_documento),''), 
	coalesce(max(nr_documento),''), 
	coalesce(max(nr_sequencia_item_docto),'') 
into STRICT	ie_origem_documento_w, 
	nr_documento_w, 
	nr_seq_docto_w 
from	movimento_estoque 
where	nr_movimento_estoque = nr_movimento_estoque_p;
 
if (nr_documento_w IS NOT NULL AND nr_documento_w::text <> '') then 
	if (ie_origem_documento_w = '3') then 
 
		select	coalesce(max(nr_atendimento),'') 
		into STRICT	nr_atendimento_w 
		from	movimento_estoque 
		where	nr_movimento_estoque = nr_movimento_estoque_p;
			 
		if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then 
			begin 
			select	obter_dados_atendimento(nr_atendimento_w,'MR') 
			into STRICT	cd_pessoa_fisica_w 
			;
 
			select	coalesce(max(obter_nome_pf(cd_pessoa_fisica_w)),'') 
			into STRICT	nm_paciente_w 
			;
			end;
		elsif (coalesce(nr_atendimento_w::text, '') = '') then 
			begin 
			if (coalesce(nr_seq_docto_w,999) = 999) then 
				select	obter_dados_atendimento(nr_documento_w,'MR') 
				into STRICT	cd_pessoa_fisica_w 
				;
				 
			end if;
			if (coalesce(cd_pessoa_fisica_w::text, '') = '') then 
				select	obter_medico_prescricao(nr_documento_w,'C') 
				into STRICT	cd_pessoa_fisica_w 
				;
			end if;
 
			if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then 
				select max(SUBSTR(OBTER_NOME_PF(cd_pessoa_fisica), 0, 200)) 
				into STRICT	nm_paciente_w 
				from	pessoa_fisica 
				where	cd_pessoa_fisica = cd_pessoa_fisica_w;
			end if;
			end;	
		end if;
	end if;
end if;
 
RETURN nm_paciente_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
