CREATE OR REPLACE FUNCTION est_obter_paciente_movto ( nr_movimento_estoque_p bigint) RETURNS varchar AS $body$
DECLARE

 
 
cd_pessoa_fisica_w	varchar(10);
nm_paciente_w		varchar(255);
nr_atendimento_w		bigint;
ie_origem_documento_w	varchar(3);
nr_documento_w		bigint;
nr_seq_docto_w		bigint;


BEGIN 
begin 
select	ie_origem_documento, 
	nr_documento, 
	nr_sequencia_item_docto, 
	nr_atendimento 
into STRICT	ie_origem_documento_w, 
	nr_documento_w, 
	nr_seq_docto_w, 
	nr_atendimento_w 
from	movimento_estoque 
where	nr_movimento_estoque = nr_movimento_estoque_p;
exception 
when others then 
	begin 
	nr_atendimento_w 	:= null;
	ie_origem_documento_w	:= 'X';
	end;
end;
 
if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then 
	begin 
	nm_paciente_w := substr(obter_pessoa_atendimento(nr_atendimento_w,'N'),1,255);
	end;
elsif (ie_origem_documento_w = '3') and (nr_documento_w IS NOT NULL AND nr_documento_w::text <> '') and (coalesce(nr_seq_docto_w::text, '') = '') then 
	begin 
	begin 
	select	cd_pessoa_fisica 
	into STRICT	cd_pessoa_fisica_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_documento_w;	
	exception 
	when others then 
		cd_pessoa_fisica_w := null;
	end;
	 
	if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then 
		begin 
		select	SUBSTR(OBTER_NOME_PF(CD_PESSOA_FISICA), 0, 255) 
		into STRICT	nm_paciente_w 
		from	pessoa_fisica 
		where	cd_pessoa_fisica = cd_pessoa_fisica_w;		
		exception 
		when others then 
			nm_paciente_w := null;
		end;
	end if;
	end;
end if;
 
return nm_paciente_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
