CREATE OR REPLACE FUNCTION esus_obter_dados_atendimento ( nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

					 
ds_retorno_w		varchar(255);
cd_medico_resp_w	atendimento_paciente.cd_medico_resp%type;

/* 
IE_OPCAO_P 
RES - Médico responsável do atendimento 
*/
 
 

BEGIN 
 
if (ie_opcao_p = 'RES') then 
	begin 
	 
	begin 
	select	cd_medico_resp 
	into STRICT	cd_medico_resp_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p  LIMIT 1;	
	exception 
	when others then 
		cd_medico_resp_w := '';
	end;
	 
	ds_retorno_w := substr(cd_medico_resp_w,1,255);
	 
	end;
end if;
 
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
