CREATE OR REPLACE FUNCTION esus_obter_exames_paciente ( cd_profissional_p text, cd_cbo_p text, nr_atendimento_p bigint, dt_atendimento_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(2000) := ',';
ie_campo_exame_w		esus_regra_exame_pac_cam.ie_campo_exame%type;
nr_sequencia_w			esus_regra_exame_pac_cam.nr_sequencia%type;
cd_procedimento_w		esus_regra_exame_pac_pro.cd_procedimento%type;
ie_origem_proced_w		esus_regra_exame_pac_pro.ie_origem_proced%type;
qt_proc_exame_w			bigint := 0;

c01 CURSOR FOR
	SELECT	ie_campo_exame,
		nr_sequencia
	from	esus_regra_exame_pac_cam
	where	ie_situacao = 'A'
	order by ie_campo_exame;

c02 CURSOR FOR
	SELECT	cd_procedimento,
		ie_origem_proced
	from	esus_regra_exame_pac_pro
	where	nr_seq_regra_exame = nr_sequencia_w
	order by cd_procedimento;


BEGIN

open C01;
loop
fetch C01 into
	ie_campo_exame_w,
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	open C02;
	loop
	fetch C02 into
		cd_procedimento_w,
		ie_origem_proced_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		select	count(1)
		into STRICT	qt_proc_exame_w
		from	procedimento_paciente
		where	nr_atendimento = nr_atendimento_p
		and	trunc(dt_procedimento) = dt_atendimento_p
		and	cd_medico_executor = cd_profissional_p
		and	cd_cbo = cd_cbo_p
		and	cd_procedimento = cd_procedimento_w
		and	ie_origem_proced = ie_origem_proced_w
		and	coalesce(cd_motivo_exc_conta::text, '') = ''  LIMIT 1;

		if (qt_proc_exame_w > 0) then
			ds_retorno_w := ds_retorno_w || ie_campo_exame_w || ',';
		end if;

		end;
	end loop;
	close C02;

	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
