CREATE OR REPLACE FUNCTION esus_obter_turno_data ( dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_turno_w		varchar(15);


BEGIN

if	(( to_char(dt_referencia_p,'hh24:mi') >= '07:00') and (to_char(dt_referencia_p,'hh24:mi') <= '11:59')) then
	ds_turno_w := 1; -- ManhÃ£
elsif	(( to_char(dt_referencia_p,'hh24:mi') >= '12:00') and (to_char(dt_referencia_p,'hh24:mi') <= '17:59')) then
	ds_turno_w := 2; -- Tarde
elsif	((( to_char(dt_referencia_p,'hh24:mi') >= '18:00') and (to_char(dt_referencia_p,'hh24:mi') <= '23:59')) or
	(( to_char(dt_referencia_p,'hh24:mi') >= '00:00') and (to_char(dt_referencia_p,'hh24:mi') <= '06:59')))then
	ds_turno_w := 3; -- Noite
end if;

return	ds_turno_w;

end;
