CREATE OR REPLACE FUNCTION esus_valida_exames_paciente ( cd_procedimento_p esus_ficha_pro_proced.cd_procedimento%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1) := 'S';
qt_regra_w			bigint := 0;


BEGIN

select	count(1)
into STRICT	qt_regra_w
from	esus_regra_exame_pac_cam a,
	esus_regra_exame_pac_pro b
where	b.nr_seq_regra_exame = a.nr_sequencia
and	a.ie_situacao = 'A'
and	b.cd_procedimento = coalesce(cd_procedimento_p,0)  LIMIT 1;

if (qt_regra_w > 0) then
	ds_retorno_w := 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
