SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION eup_obter_descricao_cep_html ( cd_cep_p text ) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Retornar descrição do CEP

-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [x] Outros: HTML5
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nm_logradouro_w    	varchar(40);
cd_cep_p_w		bigint;
ie_cep_internet_w	varchar(15);



BEGIN

if (cd_cep_p IS NOT NULL AND cd_cep_p::text <> '') then

	cd_cep_p_w := somente_numero(cd_cep_p);
	ie_cep_internet_w := obter_param_usuario(0, 25, wheb_usuario_pck.get_cd_perfil, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_cep_internet_w);

	if (ie_cep_internet_w = 'N') then
		select 	max(a.nm_logradouro)
		into STRICT	nm_logradouro_w
		from 	cep_logradouro a
		where 	a.cd_logradouro = cd_cep_p;
	else
		select 	max(a.nm_logradouro)
		into STRICT	nm_logradouro_w
		from 	cep_loc b,
			cep_bairro c,
			cep_log a
		where  	b.nr_sequencia      	= c.nr_seq_loc
		and   	a.cd_bairro_inicial	= c.nr_sequencia
		and   	b.nr_sequencia      	= a.nr_seq_loc
		and   	a.cd_cep            	= cd_cep_p;
	end if;
end if;

return	nm_logradouro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION eup_obter_descricao_cep_html ( cd_cep_p text ) FROM PUBLIC;

