CREATE OR REPLACE FUNCTION eup_obter_nm_usuario_cancel (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

				 
ds_retorno_w			varchar(255);				
nm_usuario_cancel_w		varchar(15);
dt_cancelamento_w		timestamp;


BEGIN 
 
if (coalesce(nr_atendimento_p,0) > 0) then 
 
	select	max(nm_usuario_cancelamento), 
		max(dt_cancelamento) 
	into STRICT	nm_usuario_cancel_w, 
		dt_cancelamento_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
	 
	if (dt_cancelamento_w IS NOT NULL AND dt_cancelamento_w::text <> '') then 
		if (coalesce(nm_usuario_cancel_w::text, '') = '') then 
			nm_usuario_cancel_w := substr(obter_texto_tasy(292844, wheb_usuario_pck.get_nr_seq_idioma),1,15);
		end if;
		ds_retorno_w := substr(obter_texto_dic_objeto(292846, wheb_usuario_pck.get_nr_seq_idioma, 'NM_USUARIO_CANCEL_W='|| nm_usuario_cancel_w),1,255);
	else 
		ds_retorno_w := '';
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
