CREATE OR REPLACE FUNCTION eup_obter_tempo_espera (ds_inicial_p text, ds_final_p text, ds_seq_urgencia_p text, ie_opcao_tempo_inicial_p bigint, ie_opcao_tempo_final_p bigint) RETURNS bigint AS $body$
DECLARE

			 
ds_retorno_w		integer;			
 

BEGIN 
 
if (ds_inicial_p IS NOT NULL AND ds_inicial_p::text <> '') and (ds_final_p IS NOT NULL AND ds_final_p::text <> '') and (ds_seq_urgencia_p IS NOT NULL AND ds_seq_urgencia_p::text <> '') and (ie_opcao_tempo_inicial_p IS NOT NULL AND ie_opcao_tempo_inicial_p::text <> '') and (ie_opcao_tempo_final_p IS NOT NULL AND ie_opcao_tempo_final_p::text <> '') then 
 
	select	count(a.soma) 
	into STRICT	ds_retorno_w 
	from	(SELECT	sum((coalesce(substr(abs(obter_min_entre_datas(a.dt_inicio_atendimento,a.dt_fim_triagem,1)),1,10),0))::numeric  + 
			    (coalesce(substr(abs(obter_min_entre_datas(a.dt_fim_triagem,a.dt_atend_medico,1)),1,10),0))::numeric  + 
			    (coalesce(substr(abs(obter_min_entre_datas(a.dt_atend_medico,a.dt_fim_consulta,1)),1,10),0))::numeric ) soma 
		 from	atendimento_ps_v a 
		 where	a.dt_entrada between to_date( ds_inicial_p, 'dd/mm/yyyy hh24:mi:ss') and to_date( ds_final_p, 'dd/mm/yyyy hh24:mi:ss') 
		 and	((a.nr_seq_triagem = ds_seq_urgencia_p) or ('0' = ds_seq_urgencia_p)) 
		 group by 	a.dt_inicio_atendimento, 
			a.dt_fim_triagem, 
			a.dt_atend_medico, 
			a.dt_fim_consulta) a 
	where	a.soma > ie_opcao_tempo_inicial_p 
	and	a.soma <= ie_opcao_tempo_final_p;
 
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
