CREATE OR REPLACE FUNCTION exibe_itens_sem_codigo (nr_seq_agenda_p text) RETURNS varchar AS $body$
DECLARE


ds_material_w	varchar(255);
ds_material_ww	varchar(255);

C01 CURSOR FOR
	SELECT	ds_material
	from	agenda_pac_desc_material
	where 	nr_seq_agenda = nr_seq_agenda_p;


BEGIN

ds_material_ww := '';

open C01;
loop
fetch C01 into
	ds_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ds_material_ww IS NOT NULL AND ds_material_ww::text <> '') then
		ds_material_ww := substr(ds_material_ww||', ',1,255);
	end if;

	ds_material_ww := substr(ds_material_ww||ds_material_w,1,255);
	end;
end loop;
close C01;

return	substr(ds_material_ww,1,255);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
