CREATE OR REPLACE FUNCTION exibe_msg_atrib_eup ( nm_atributo_p text, ds_valor_p text, nm_tabela_p text) RETURNS varchar AS $body$
DECLARE


ds_mensagem_w 	varchar(2000);

C01 CURSOR FOR
	SELECT 	substr(ds_mensagem,1,2000)
	FROM 	REGRA_MSG_ATRIB_PACIENTE
	WHERE 	nm_atributo = nm_atributo_p
	AND 	((ds_valor = ds_valor_p) or (coalesce(ds_valor::text, '') = '' and (ds_valor_p IS NOT NULL AND ds_valor_p::text <> '')))
	and	((nm_tabela = nm_tabela_p) or (coalesce(nm_tabela::text, '') = ''))
	and	((cd_perfil = obter_perfil_ativo) or (coalesce(cd_perfil::text, '') = ''))
	and 	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;


BEGIN

open C01;
loop
fetch C01 into
	ds_mensagem_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

return ds_mensagem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
