CREATE OR REPLACE FUNCTION exige_justificativa_mot_alta (cd_motivo_alta_p bigint) RETURNS varchar AS $body$
DECLARE


ie_exige_justificativa_w	varchar(1);


BEGIN

ie_exige_justificativa_w := 'N';

if (coalesce(cd_motivo_alta_p,0) > 0) then

	select	coalesce(max(ie_obriga_justificativa),'N')
	into STRICT	ie_exige_justificativa_w
	from	motivo_alta
	where	cd_motivo_alta = cd_motivo_alta_p;

end if;

return	ie_exige_justificativa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
