CREATE OR REPLACE FUNCTION existe_cid_log_integracao (nr_atendimento_p bigint, cd_doenca_retorno_p text) RETURNS varchar AS $body$
DECLARE


ie_utilizado_w varchar(01) := 'N';


BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '' AND cd_doenca_retorno_p IS NOT NULL AND cd_doenca_retorno_p::text <> '') then
	
	select coalesce(max(a.ie_utilizado), 'N') 
	  into STRICT ie_utilizado_w
	  from log_integracao_cid a
	  where a.nr_atendimento = nr_atendimento_p 
	   and a.cd_doenca_retorno = cd_doenca_retorno_p
	   and a.nr_registro_origem = (SELECT max(b.nr_registro_origem) from log_integracao_cid b where b.nr_atendimento = nr_atendimento_p and b.cd_doenca_retorno = cd_doenca_retorno_p);

end if;

return ie_utilizado_w;

end;
