CREATE OR REPLACE FUNCTION existe_login_usuario_atx (ds_login_p text) RETURNS varchar AS $body$
DECLARE

qt_registro_w bigint;ds_login_w	varchar(50) := upper(ds_login_p);

BEGIN
select 	count(1)
into STRICT	qt_registro_w
from	usuario
where	upper(nm_usuario) = ds_login_w;

if (qt_registro_w	= 0) then
	select 	count(1)
	into STRICT	qt_registro_w
	from	usuario
	where	upper(ds_login) = ds_login_w;
end if;

if (qt_registro_w > 0 ) then
	return 'S';
else
	return 'N';
end if;
END;
$body$
LANGUAGE PLPGSQL
