CREATE OR REPLACE FUNCTION existe_nut_reserva_pf ( nr_seq_servico_p bigint, dt_inicial_p timestamp, dt_final_p timestamp, cd_pessoa_fisica_p text, nr_seq_restaurante_p bigint) RETURNS varchar AS $body$
DECLARE


qt_reserva_w	bigint := 0;
ie_retorno_w	varchar(2);


BEGIN

if	((nr_seq_servico_p IS NOT NULL AND nr_seq_servico_p::text <> '') and (dt_inicial_p IS NOT NULL AND dt_inicial_p::text <> '') and (dt_final_p IS NOT NULL AND dt_final_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '')) then

	SELECT	COUNT(*)
	into STRICT	qt_reserva_w
	FROM	nut_reserva_pf a,
		nut_reserva b
	WHERE 	a.nr_seq_nut_reserva = b.nr_sequencia
	AND	a.cd_pessoa_fisica = cd_pessoa_fisica_p
	AND   	b.nr_seq_servico = nr_seq_servico_p
	AND	((b.nr_seq_restaurante = nr_seq_restaurante_p) OR (nr_seq_restaurante_p = 0))
	AND	b.dt_servico BETWEEN dt_inicial_p AND fim_dia(dt_final_p);

end if;

if (qt_reserva_w > 0) then
	ie_retorno_w := 'S';
else
	ie_retorno_w := 'N';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
