CREATE OR REPLACE FUNCTION far_obter_conv_cliente (cd_pessoa_fisica_p text) RETURNS bigint AS $body$
DECLARE


nr_retorno_w		bigint;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_retorno_w
from	far_contrato_conv a
where	trunc(clock_timestamp()) between dt_inicio_vigencia and dt_final_vigencia
and	exists (SELECT	1
		from	far_contrato_partic x
		where	x.nr_seq_contrato_conv = a.nr_sequencia
		and	x.cd_pessoa_fisica = cd_pessoa_fisica_p);

return nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
