CREATE OR REPLACE FUNCTION far_obter_dados_pedido (nr_seq_pedido_p bigint, ie_retorno_p text) RETURNS varchar AS $body$
DECLARE

/*DE - Data entrega real*/

dt_entrega_real_w	timestamp;

ds_retorno_w	varchar(255);

BEGIN
if (ie_retorno_p = 'DE') then
	select	max(dt_entrega_real)
	into STRICT	dt_entrega_real_w
	from	far_rom_entrega_pedido
	where	nr_seq_pedido = nr_seq_pedido_p;
	ds_retorno_w := to_char(dt_entrega_real_w,'dd/mm/yyyy hh24:mi:ss');
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
