SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION far_obter_dados_venda ( nr_seq_venda_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*
IE_OPCAO_P:
'NF'	- Numero de sequencia da nota da venda (max)
'NR'	- Numero da nota fiscal da venda (max)
'T'	- Numero do titulo (max)
*/
ds_retorno_w	varchar(4000);


BEGIN

if (nr_seq_venda_p IS NOT NULL AND nr_seq_venda_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then
	if ('NR' = upper(ie_opcao_p)) then
		select	max(nr_sequencia)
		into STRICT	ds_retorno_w
		from	nota_fiscal
		where	nr_seq_far_venda = nr_seq_venda_p
		and	ie_situacao = '1';
	elsif ('NF' = upper(ie_opcao_p)) then
		select	max(nr_nota_fiscal)
		into STRICT	ds_retorno_w
		from	nota_fiscal
		where	nr_seq_far_venda = nr_seq_venda_p
		and	ie_situacao = '1';
	elsif ('T' = upper(ie_opcao_p)) then
		select	max(a.nr_titulo)
		into STRICT	ds_retorno_w
		from	titulo_receber a,
			nota_fiscal b
		where	a.nr_seq_nf_saida = b.nr_sequencia
		and	b.nr_seq_far_venda = nr_seq_venda_p
		and	b.ie_situacao = '1';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
