CREATE OR REPLACE FUNCTION far_obter_qtde_reserva (cd_material_p bigint, cd_local_estoque_p bigint) RETURNS bigint AS $body$
DECLARE


qt_retorno_w		double precision;
cd_local_estoque_w	smallint;


BEGIN

cd_local_estoque_w	:= coalesce(cd_local_estoque_p,0);

select	coalesce(sum(qt_material),0)
into STRICT	qt_retorno_w
from	far_reserva_mat
where	cd_material = cd_material_p
and (cd_local_estoque = cd_local_estoque_w or cd_local_estoque_w = 0)
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and	coalesce(dt_baixa::text, '') = '';

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
