CREATE OR REPLACE FUNCTION far_obter_se_item_orcamento ( nr_seq_pedido_p bigint, nr_seq_item_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
qt_item_orcamento_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_item_orcamento_w
from	far_pedido_item
where	nr_seq_pedido = nr_seq_pedido_p
and	nr_seq_item = nr_seq_item_p
and	cd_material = cd_material_p;

if (qt_item_orcamento_w > 0) then
	ds_retorno_w	:= 'S';
else
	ds_retorno_w	:= 'N';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
