SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION far_obter_se_logon_confirmado ( nr_seq_log_acesso_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w			varchar(10) := 'N';
qt_existe_w			bigint;
qt_pedido_confirmado_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	far_log_acesso_site_acao a
where	a.nr_seq_log_acesso = nr_seq_log_acesso_p;

if (qt_existe_w > 0) then
	select	count(*)
	into STRICT	qt_pedido_confirmado_w
	from	far_log_acesso_site_acao a
	where	a.nr_seq_log_acesso = nr_seq_log_acesso_p
	and	a.ie_acao = 'COP'; -- Ação que é informada quando gerado a confirmação do pedido
	if (qt_pedido_confirmado_w > 0) then
		ie_retorno_w := 'S';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
