CREATE OR REPLACE FUNCTION far_obter_se_promocao_existe ( nr_seq_nova_promo_p bigint, cd_material_p bigint, cd_grupo_material_p bigint, cd_subgrupo_material_p bigint, cd_classe_material_p bigint, dt_inicio_vigencia_p timestamp, dt_final_vigencia_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
qt_existe_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	far_regra_promocao
where	nr_sequencia <> nr_seq_nova_promo_p
and	coalesce(cd_material,0) 		= coalesce(cd_material_p,0)
and	coalesce(cd_grupo_material,0)	= coalesce(cd_grupo_material_p,0)
and	coalesce(cd_subgrupo_material,0) 	= coalesce(cd_subgrupo_material_p,0)
and	coalesce(cd_classe_material,0) 	= coalesce(cd_classe_material_p,0)
and	((dt_inicio_vigencia_p between dt_inicio_vigencia and dt_final_vigencia) or (dt_final_vigencia_p between dt_inicio_vigencia and dt_final_vigencia));

ds_retorno_w	:= 'N';
if (qt_existe_w > 0) then
	ds_retorno_w	:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
