CREATE OR REPLACE FUNCTION far_obter_status_pedido ( nr_seq_pedido_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
dt_pedido_w		timestamp;
dt_inicio_atendimento_w	timestamp;
dt_fechamento_w		timestamp;
dt_saida_w		timestamp;
dt_entrega_real_w		timestamp;
ie_entregar_w		varchar(1);


BEGIN

select	dt_pedido,
	dt_inicio_atendimento,
	dt_fechamento,
	dt_saida,
	dt_entrega_real,
	coalesce(ie_entregar,'N')
into STRICT	dt_pedido_w,
	dt_inicio_atendimento_w,
	dt_fechamento_w,
	dt_saida_w,
	dt_entrega_real_w,
	ie_entregar_w
from	far_pedido_v
where	nr_sequencia = nr_seq_pedido_p;

if (ie_entregar_w = 'S') then
	begin

	if (dt_entrega_real_w IS NOT NULL AND dt_entrega_real_w::text <> '') then
		ds_retorno_w	:= Wheb_mensagem_pck.get_texto(308429); --'Atendido/Entregue';
	elsif (dt_saida_w IS NOT NULL AND dt_saida_w::text <> '') then
		ds_retorno_w	:= Wheb_mensagem_pck.get_texto(308430); --'Saiu para entrega';
	elsif (dt_fechamento_w IS NOT NULL AND dt_fechamento_w::text <> '') then
		ds_retorno_w	:= Wheb_mensagem_pck.get_texto(308431); --'Pendente de entrega';
	elsif (dt_inicio_atendimento_w IS NOT NULL AND dt_inicio_atendimento_w::text <> '') then
		ds_retorno_w	:= Wheb_mensagem_pck.get_texto(308432); --'Em atendimento';
	elsif (dt_pedido_w IS NOT NULL AND dt_pedido_w::text <> '') then
		ds_retorno_w	:= Wheb_mensagem_pck.get_texto(308433); --'Aguardando atendimento';
	end if;

	end;
else
	begin

	if (dt_fechamento_w IS NOT NULL AND dt_fechamento_w::text <> '') then
		ds_retorno_w	:= Wheb_mensagem_pck.get_texto(308429); --'Atendido/Entregue';
	elsif (dt_inicio_atendimento_w IS NOT NULL AND dt_inicio_atendimento_w::text <> '') then
		ds_retorno_w	:= Wheb_mensagem_pck.get_texto(308432); --'Em atendimento';
	elsif (dt_pedido_w IS NOT NULL AND dt_pedido_w::text <> '') then
		ds_retorno_w	:= Wheb_mensagem_pck.get_texto(308433); --'Aguardando atendimento';
	end if;

	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
