CREATE OR REPLACE FUNCTION far_obter_tributo_nota_item (nr_nota_fiscal_p bigint, cd_material_p bigint) RETURNS bigint AS $body$
DECLARE


/*
1 - Quando há ICMS (indicar alíquota e valor)  / Produto isento de ICMS (deixar alíquota e valor zerados) ;
2 - Quando há ST (Substituição Tributária) e já está embutido no preço;
3 - Quando há ST (Substituição Tributária) e não está embutido no preço.

Integração IMS

*/
qt_nf_w			bigint;
qt_tributo_w		bigint;
cd_tritubo_w		integer;
ie_tipo_tributo_w		varchar(15);
retorno_w			bigint:= 0;


BEGIN

select	count(*)
into STRICT	qt_nf_w
from	nota_fiscal_item_trib f,
	nota_fiscal_item i
where	f.nr_sequencia = i.nr_sequencia
and	f.nr_item_nf = i.nr_item_nf
and	f.nr_sequencia = nr_nota_fiscal_p
and	i.cd_material = cd_material_p;


if (qt_nf_w > 0) then

	select	max(cd_tributo)
	into STRICT	cd_tritubo_w
	from	nota_fiscal_item_trib f,
		nota_fiscal_item i
	where	f.nr_sequencia = i.nr_sequencia
	and	f.nr_item_nf = i.nr_item_nf
	and	f.nr_sequencia = nr_nota_fiscal_p
	and	i.cd_material = cd_material_p;

	select	count(*)
	into STRICT	qt_tributo_w
	from	tributo
	where	cd_tributo = cd_tritubo_w;

	if (qt_tributo_w > 0) then

		select CASE WHEN ie_tipo_tributo='ICMS' THEN 1 WHEN ie_tipo_tributo='ICMSST' THEN 3  ELSE 0 END
		into STRICT	retorno_w
		from	tributo
		where	cd_tributo = cd_tritubo_w;

	end if;

end if;

return	coalesce(retorno_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
