SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION far_obter_tx_entrega ( nr_seq_pedido_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w	double precision	:= 0;
cd_cep_w	varchar(15);


BEGIN

select	max(coalesce(cd_cep,'0'))
into STRICT	cd_cep_w
from	far_pedido_endereco
where	nr_seq_pedido = nr_seq_pedido_p;

if (cd_cep_w <> '0') then
	begin

	select	max(coalesce(tx_entrega,0))
	into STRICT	vl_retorno_w
	from	far_grupo_entrega a
	where	exists (SELECT	1
			from	far_grupo_entrega_cep x
			where	x.nr_seq_grupo = a.nr_sequencia
			and	x.cd_cep = cd_cep_w);
	end;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION far_obter_tx_entrega ( nr_seq_pedido_p bigint) FROM PUBLIC;

