CREATE OR REPLACE FUNCTION far_obter_valores_pedido ( nr_seq_pedido_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		double precision;
vl_desconto_w		double precision;
vl_desconto_item_w	double precision;


BEGIN

if (ie_opcao_p = 'D') then
	begin

	select	coalesce(sum(vl_desconto),0)
	into STRICT	vl_desconto_item_w
	from	far_pedido_item
	where	nr_seq_pedido = nr_seq_pedido_p;

	select	coalesce(max(vl_desconto),0)
	into STRICT	vl_desconto_w
	from	far_pedido
	where	nr_sequencia = nr_seq_pedido_p;

	vl_retorno_w	:= round((vl_desconto_w + vl_desconto_item_w)::numeric,2);

	end;

elsif (ie_opcao_p = 'S') then
	begin

	select	round((sum(vl_total_item))::numeric,2)
	into STRICT	vl_retorno_w
	from	far_pedido_item
	where	nr_seq_pedido = nr_seq_pedido_p;

	end;
end if;

return vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
