SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION far_obter_veiculo_entregador (nr_seq_entregador_p bigint, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


ds_retorno_w		bigint;


BEGIN

select	max(coalesce(a.nr_sequencia,null))
into STRICT	ds_retorno_w
from	eme_veiculo a
where	a.cd_estabelecimento = cd_estabelecimento_p
and	exists (SELECT 	1
	       from     	man_veiculo_entregador x
	       where    	x.nr_seq_veiculo = a.nr_sequencia
	       and      	x.nr_seq_entregador = nr_seq_entregador_p
	       and      	x.dt_inicio <= trunc(clock_timestamp())
	       and      	coalesce(x.dt_final,clock_timestamp()) >= trunc(clock_timestamp()));

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
