CREATE OR REPLACE FUNCTION far_obter_vendedor_usuario (nm_usuario_p text) RETURNS bigint AS $body$
DECLARE


cd_retorno_w		bigint;
cd_pessoa_fisica_w	varchar(10);


BEGIN

select	cd_pessoa_fisica
into STRICT	cd_pessoa_fisica_w
from	usuario
where	upper(nm_usuario) = upper(nm_usuario_p);

if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
	begin

	select	max(coalesce(nr_sequencia,null))
	into STRICT	cd_retorno_w
	from	far_vendedor
	where	cd_pessoa_fisica = cd_pessoa_fisica_w;

	end;
end if;

return	cd_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
