SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION far_obter_vl_desconto_conv ( cd_material_p bigint, cd_estabelecimento_p bigint, nr_seq_contrato_conv_p bigint) RETURNS bigint AS $body$
DECLARE


cd_tab_preco_mat_w 		smallint;
vl_preco_w 			double precision;
vl_desconto_w			double precision;
tx_ajuste_w			double precision;
cd_grupo_material_w		smallint;
cd_subgrupo_material_w		smallint;
cd_classe_material_w 		integer;
tx_ajuste_contrato_conv		double precision;

c01 CURSOR FOR
SELECT	cd_tab_preco_mat,
	coalesce(tx_ajuste,0)
from	far_regra_preco_mat a
where	coalesce(a.cd_grupo_material,cd_grupo_material_w) 		= cd_grupo_material_w
and	coalesce(a.cd_subgrupo_material,cd_subgrupo_material_w)	= cd_subgrupo_material_w
and	coalesce(a.cd_classe_material,cd_classe_material_w) 		= cd_classe_material_w
and	coalesce(a.cd_material,cd_material_p) 			= cd_material_p
and	a.cd_estabelecimento = cd_estabelecimento_p
and	exists (select 1 from preco_material x where x.cd_material = cd_material_p and x.cd_tab_preco_mat = a.cd_tab_preco_mat)
order by 	coalesce(cd_material,0),
	coalesce(cd_classe_material,0),
	coalesce(cd_subgrupo_material,0),
	coalesce(cd_grupo_material,0);

C02 CURSOR FOR
	SELECT	coalesce(a.TX_AJUSTE,0)
	from	FAR_CONTRATO_PRECO a
	where	a.nr_seq_contrato_conv = nr_seq_contrato_conv_p
	and	coalesce(a.cd_grupo_material,cd_grupo_material_w) 		= cd_grupo_material_w
	and	coalesce(a.cd_subgrupo_material,cd_subgrupo_material_w)	= cd_subgrupo_material_w
	and	coalesce(a.cd_classe_material,cd_classe_material_w) 	= cd_classe_material_w
	and	coalesce(a.cd_material,cd_material_p) = cd_material_p
	order by coalesce(cd_material,0) asc,
		coalesce(cd_classe_material,0) asc,
		coalesce(cd_subgrupo_material,0) asc,
		coalesce(cd_grupo_material,0) asc;


BEGIN
select 	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w
from 	estrutura_material_v
where	cd_material = cd_material_p;

open c01;
loop
fetch c01 into
	cd_tab_preco_mat_w,
	tx_ajuste_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;

if (nr_seq_contrato_conv_p IS NOT NULL AND nr_seq_contrato_conv_p::text <> '') then
	begin
	open C02;
	loop
	fetch C02 into
		tx_ajuste_contrato_conv;
	EXIT WHEN NOT FOUND; /* apply on C02 */
	end loop;
	close C02;
	end;
end if;

select 	vl_preco_venda
into STRICT	vl_preco_w
from 	preco_material a
where 	cd_material = cd_material_p
and	cd_tab_preco_mat = cd_tab_preco_mat_w
and	ie_situacao = 'A'
and	trunc(dt_inicio_vigencia) =	(SELECT	max(x.dt_inicio_vigencia)
				from	preco_material x
				where	x.cd_tab_preco_mat = a.cd_tab_preco_mat
				and	x.cd_material = a.cd_material
				and	x.ie_situacao = 'A'
				and	trunc(x.dt_inicio_vigencia) <= trunc(clock_timestamp()));

vl_preco_w := coalesce(vl_preco_w,0) + (dividir((vl_preco_w * coalesce(tx_ajuste_w,0)),100));
vl_desconto_w := (dividir((vl_preco_w * coalesce(tx_ajuste_contrato_conv,0)),100));

return coalesce(vl_desconto_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
