CREATE OR REPLACE FUNCTION fa_consiste_entrega_periodo ( dt_periodo_inicial_p timestamp, dt_periodo_final_p timestamp, nr_seq_receita_p bigint, nr_seq_entrega_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1);
ie_pmc_w	varchar(1);

BEGIN

--verificar se a entrega eh pmc
SELECT 	CASE WHEN coalesce(max(NR_SEQ_PACIENTE_PMC)::text, '') = '' THEN 'N'  ELSE 'S' END
into STRICT	ie_pmc_w
FROM   	fa_paciente_entrega a,
	fa_entrega_medicacao b
WHERE  	b.nr_seq_paciente_entrega  = a.nr_sequencia
AND	b.nr_sequencia 		   = nr_seq_entrega_p
and	coalesce(b.dt_cancelamento::text, '') = '';

SELECT	CASE WHEN COUNT(*)=0 THEN 'S'  ELSE 'N' END
into STRICT	ie_retorno_w
FROM	fa_entrega_medicacao a
WHERE	a.nr_seq_receita_amb = nr_seq_receita_p
AND	a.nr_sequencia <> coalesce(nr_seq_entrega_p,0)
and	coalesce(a.dt_cancelamento::text, '') = ''
AND (trunc(dt_periodo_inicial_p) BETWEEN trunc(a.dt_periodo_inicial) AND trunc(a.dt_periodo_final))
AND (trunc(dt_periodo_final_p)   BETWEEN trunc(a.dt_periodo_inicial) AND trunc(a.dt_periodo_final))
AND	(( 	(ie_pmc_w = 'N')
		and	not EXISTS ( 	SELECT 	1
				FROM   	fa_entrega_medicacao_item x
				WHERE  	x.nr_seq_fa_entrega = a.nr_sequencia
				AND 	((x.qt_gerado > x.qt_dispensar) OR (x.dt_cancelamento IS NOT NULL AND x.dt_cancelamento::text <> '')))
		AND   EXISTS ( 	SELECT 1
			FROM 	fa_paciente_entrega x
			WHERE	x.nr_sequencia = a.nr_seq_paciente_entrega
			AND  	coalesce(x.nr_seq_paciente_pmc::text, '') = '')
	)
or	((ie_pmc_w = 'S')
	AND	EXISTS (	SELECT 1
				FROM   fa_entrega_medicacao_item x
				WHERE  x.nr_seq_fa_entrega 	= a.nr_sequencia
				AND    fa_obter_se_medic_pac_pmc(nr_seq_fa_entrega,cd_material) = 'S')
	));


return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION fa_consiste_entrega_periodo ( dt_periodo_inicial_p timestamp, dt_periodo_final_p timestamp, nr_seq_receita_p bigint, nr_seq_entrega_p bigint, cd_material_p bigint) FROM PUBLIC;

