CREATE OR REPLACE FUNCTION fa_consistir_estoque_medic ( cd_material_p bigint, qt_material_p bigint, cd_unidade_baixa_p text, cd_local_estoque_p text, cd_estabelecimento_p text, nr_seq_lote_p bigint default null) RETURNS varchar AS $body$
DECLARE


qt_estoque_w			double precision;
cd_unidade_medida_estoque_w	varchar(30);
cd_material_estoque_w		integer;
ds_retorno_w			varchar(10);
qt_material_w			bigint;


BEGIN
ds_retorno_w := 'N';
qt_material_w := qt_material_p;
select	substr(obter_dados_material_estab(cd_material,cd_estabelecimento_p,'UME'),1,30) cd_unidade_medida_estoque,
	cd_material_estoque
into STRICT	cd_unidade_medida_estoque_w,
	cd_material_estoque_w
from	material
where	cd_material = cd_material_p;

if (coalesce(cd_local_estoque_p, 0) > 0) then
	if (cd_unidade_baixa_p <> cd_unidade_medida_estoque_w) then
		qt_material_w := obter_dose_convertida(cd_material_p,qt_material_w,cd_unidade_baixa_p,cd_unidade_medida_estoque_w);
	end if;

	qt_estoque_w	:= obter_saldo_disp_estoque(cd_estabelecimento_p,cd_material_estoque_w,cd_local_estoque_p,trunc(clock_timestamp(),'mm'),nr_seq_lote_p);

	if (qt_estoque_w >= qt_material_w) then
		ds_retorno_w := 'S';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
