CREATE OR REPLACE FUNCTION fa_obter_atend_medic_item ( nr_seq_medic_item_p bigint) RETURNS bigint AS $body$
DECLARE

nr_atendimento_w	bigint;

BEGIN
select max(a.nr_atendimento)
into STRICT	nr_atendimento_w
from   fa_paciente_entrega a,
       fa_entrega_medicacao b,
       fa_entrega_medicacao_item c
where  a.nr_sequencia = b.NR_SEQ_PACIENTE_ENTREGA
and	b.nr_sequencia = c.NR_SEQ_FA_ENTREGA
