SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION fa_obter_dados_medicamento (nr_seq_medic_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(255);
cd_material_w	varchar(100);
ds_material_w	varchar(255);

BEGIN

select 	cd_material,
	substr(obter_desc_material(cd_material),1,255)
into STRICT	cd_material_w,
	ds_material_w
from	FA_MEDIC_FARMACIA_AMB
where	nr_sequencia = nr_seq_medic_p;


if (ie_tipo_p = 'M') then
	ds_retorno_w := cd_material_w;
elsif (ie_tipo_p = 'D') then
	ds_retorno_w := ds_material_w;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
