CREATE OR REPLACE FUNCTION fa_obter_dados_receita (nr_seq_receita_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE

			 
nr_serie_w		varchar(15);
nr_receita_w		varchar(15);
cd_pessoa_fisica_w	bigint;
ds_retorno_w		varchar(255);


BEGIN 
 
/* 
S = Serie 
R = Receita 
CP = Codigo Pessoa Fisica 
NP= Nome Pessoa Fisica 
*/
 
 
select 	nr_serie, 
	nr_receita, 
	cd_pessoa_fisica 
into STRICT	nr_serie_w, 
	nr_receita_w, 
	cd_pessoa_fisica_w 
from	fa_receita_farmacia 
where	nr_sequencia = nr_seq_receita_p;
 
if (ie_tipo_p = 'S') then 
	ds_retorno_w := nr_serie_w;
elsif (ie_tipo_p = 'R') then 
	ds_retorno_w := nr_receita_w;
elsif (ie_tipo_p = 'CP') then 
	ds_retorno_w := cd_pessoa_fisica_w;
elsif (ie_tipo_p = 'NP') then 
	ds_retorno_w := substr(obter_nome_pessoa_fisica(cd_pessoa_fisica_w,''),1,255);
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
