SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION fa_obter_dado_indicador_atendi (nr_atendimento_p bigint, ie_tabela_valor_p text, ie_resultado_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(150);
cd_categoria_w		bigint;
cd_convenio_w		integer;
nm_convenio_w		varchar(150);
cd_setor_atendimento_w	integer;
nm_setor_atendimento_w	varchar(150);

/*ie_tabela_valor_p
	ACC	-> atend_categoria_convenio
		ie_resultado_p:
			CAT	-> Categoria convênio
			CONV	-> Convênio
			NMCONV	-> Nome convênio

	APU	-> atend_paciente_unidade
		ie_resultado_p:
			CD	-> Código setor atendimento
			NM	-> Nome setor atendimento
*/
BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (ie_tabela_valor_p IS NOT NULL AND ie_tabela_valor_p::text <> '') then
	if (ie_tabela_valor_p = 'ACC') then
		select	max(b.cd_categoria),
			max(b.cd_convenio),
			max(obter_nome_convenio(b.cd_convenio))
		into STRICT	cd_categoria_w,
			cd_convenio_w,
			nm_convenio_w
		from	atend_categoria_convenio b
		where	b.nr_atendimento = nr_atendimento_p;

		if (ie_resultado_p = 'CAT') then
			ds_retorno_w	:=	to_char(cd_categoria_w);
		elsif (ie_resultado_p = 'CONV') then
			ds_retorno_w	:=	to_char(cd_convenio_w);
		elsif (ie_resultado_p = 'NMCONV') then
			ds_retorno_w	:=	nm_convenio_w;
		end if;


	elsif (ie_tabela_valor_p = 'APU') then
		select	max(c.cd_setor_atendimento),
			max(obter_nome_setor(c.cd_setor_atendimento))
		into STRICT	cd_setor_atendimento_w,
			nm_setor_atendimento_w
		from	atend_paciente_unidade c
		where	c.nr_atendimento = nr_atendimento_p;

		if (ie_resultado_p = 'CD') then
			ds_retorno_w	:=	to_char(cd_setor_atendimento_w);
		elsif (ie_resultado_p = 'NM') then
			ds_retorno_w	:=	nm_setor_atendimento_w;
		end if;

	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
