CREATE OR REPLACE FUNCTION fa_obter_data_retorno_item ( nr_seq_medic_item_p bigint) RETURNS timestamp AS $body$
DECLARE


qt_gerado_w		integer;
qt_dispensar_w		integer;
nr_dias_util_w		smallint;
dt_periodo_inicial_w	timestamp;
dt_prevista_retorno_w	timestamp;
dt_retorno_w		timestamp;


BEGIN

if (nr_seq_medic_item_p IS NOT NULL AND nr_seq_medic_item_p::text <> '') then

	select	a.qt_gerado,
		a.qt_dispensar,
		b.nr_dias_util,
		b.dt_periodo_inicial,
		b.dt_prevista_retorno
	into STRICT	qt_gerado_w,
		qt_dispensar_w,
		nr_dias_util_w,
		dt_periodo_inicial_w,
		dt_prevista_retorno_w
	from	fa_entrega_medicacao_item a,
		fa_entrega_medicacao b
	where	a.nr_seq_fa_entrega = b.nr_sequencia
	and	a.nr_sequencia = nr_seq_medic_item_p;

	if (qt_gerado_w > 0) and (qt_dispensar_w > 0) then
		dt_retorno_w := trunc(dt_periodo_inicial_w + (qt_dispensar_w/(qt_gerado_w/nr_dias_util_w)) - 1);
	else
		dt_retorno_w := trunc(dt_prevista_retorno_w);
	end if;

end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
