CREATE OR REPLACE FUNCTION fa_obter_material_lib_sus (cd_material_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

			 
ds_retorno_w	varchar(1);
ie_possui_laudo_w	varchar(1);
ie_exige_laudo_w	varchar(1);


BEGIN 
ie_possui_laudo_w := 'S';
if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then 
	select	coalesce(max('S'),'N') 
	into STRICT	ie_exige_laudo_w 
	from 	fa_medic_farmacia_amb 
	where 	ie_exige_laudo_sus = 'S' 
	and 	cd_material	  = cd_material_p;
end if;
 
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (ie_exige_laudo_w = 'S') then 
	select	count(*) 
	into STRICT 	ie_possui_laudo_w 
	from 	sus_material_opm c, 
		procedimento b, 
		sus_laudo_paciente a 
	where 	a.cd_procedimento_solic = b.cd_procedimento 
	and  	a.ie_origem_proced	= b.ie_origem_proced 
	and  	b.cd_procedimento	= c.cd_procedimento 
	and  	b.ie_origem_proced	= c.ie_origem_proced 
	and  	a.cd_pessoa_fisica 	= cd_pessoa_fisica_p 
	and  	c.cd_material		= cd_material_p;
	 
	if (ie_possui_laudo_w = '0') then 
		ie_possui_laudo_w := 'N';
	else 
		ie_possui_laudo_w := 'S';
	end if;
	 
end if;
 
return	ie_possui_laudo_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
