CREATE OR REPLACE FUNCTION fa_obter_mensagem ( ds_mensagem_p text) RETURNS varchar AS $body$
DECLARE


ds_mensagem_w	varchar(255);


BEGIN
if (ds_mensagem_p IS NOT NULL AND ds_mensagem_p::text <> '') then
	ds_mensagem_w	:= wheb_mensagem_pck.get_texto(somente_numero(substr(ds_mensagem_p,1,6)), substr(ds_mensagem_p,7,255));
end if;

return	ds_mensagem_w;

end;
