CREATE OR REPLACE FUNCTION fa_obter_pessoa_receita_pmc ( nr_seq_receita_p bigint) RETURNS varchar AS $body$
DECLARE


cd_pessoa_fisica_w	varchar(14);


BEGIN

if (nr_seq_receita_p IS NOT NULL AND nr_seq_receita_p::text <> '') then
	SELECT 	max(b.cd_pessoa_fisica)
	into STRICT	cd_pessoa_fisica_w
	FROM   	fa_paciente_entrega a,
		fa_entrega_medicacao b,
		fa_paciente_pmc c
	WHERE  	a.nr_sequencia  	  = b.nr_seq_paciente_entrega
	AND	a.cd_pessoa_fisica 	  = c.cd_pessoa_fisica
	AND    	b.nr_sequencia		  = nr_seq_receita_p;
end if;


return	cd_pessoa_fisica_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
