SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION fa_obter_prox_data_retorno (nr_seq_entr_pac_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(255);
nr_receita_w		varchar(255);
nr_seq_receita_w	bigint;
nr_seq_entrega_w	bigint;
dt_prevista_retorno	varchar(255);

C01 CURSOR FOR
SELECT 	r.nr_sequencia,
	r.nr_receita,
	MAX(f.nr_sequencia),
	MAX(to_char(f.dt_prevista_retorno,'dd/mm/yyyy'))
FROM   	fa_entrega_medicacao f,
	fa_paciente_entrega e,
	fa_receita_farmacia r
WHERE  	f.nr_seq_paciente_entrega = e.nr_sequencia
AND	r.nr_sequencia = f.nr_seq_receita_amb
AND	e.nr_sequencia = nr_seq_entr_pac_p
GROUP BY r.nr_sequencia,
	 r.nr_receita
ORDER BY 3;


BEGIN
ds_retorno_w := '';
open C01;
loop
fetch C01 into
	nr_seq_receita_w,
	nr_receita_w,
	nr_seq_entrega_w,
	dt_prevista_retorno;
EXIT WHEN NOT FOUND; /* apply on C01 */
	ds_retorno_w :=  ds_retorno_w || nr_receita_w || '-> ' || dt_prevista_retorno || '; ';
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
