CREATE OR REPLACE FUNCTION fa_obter_qtd_entrega_periodo ( dt_periodo_inicial_p timestamp, dt_periodo_final_p timestamp, nr_seq_receita_p bigint, nr_seq_entrega_p bigint, cd_material_p bigint) RETURNS bigint AS $body$
DECLARE


ie_retorno_w		varchar(1);
nr_seq_anterior_w	bigint;
qt_dispensar_ant_w	integer	:= 0;
cd_pessoa_fisica_w	varchar(10);

BEGIN

select 	max(cd_pessoa_fisica)
into STRICT	cd_pessoa_fisica_w
from	fa_entrega_medicacao
where	nr_sequencia = nr_seq_entrega_p;


SELECT	coalesce(max(a.nr_sequencia),0)
into STRICT	nr_seq_anterior_w
FROM	fa_entrega_medicacao a
WHERE	a.cd_pessoa_fisica = cd_pessoa_fisica_w
AND	a.nr_sequencia <> coalesce(nr_seq_entrega_p,0)
AND (trunc(dt_periodo_inicial_p) BETWEEN trunc(a.dt_periodo_inicial) AND trunc(a.dt_periodo_final))
AND (trunc(dt_periodo_final_p)   BETWEEN trunc(a.dt_periodo_inicial) AND trunc(a.dt_periodo_final))
AND	 EXISTS ( SELECT 1
	   	 FROM   fa_entrega_medicacao_item x
		 WHERE  x.nr_seq_fa_entrega 	= a.nr_sequencia
		 AND	x.cd_material		= cd_material_p
		 AND	x.qt_gerado > x.qt_dispensar);

if (nr_seq_anterior_w IS NOT NULL AND nr_seq_anterior_w::text <> '') then
	SELECT 	coalesce(MAX(x.qt_gerado),0) - coalesce(MAX(qt_dispensar),0)
	into STRICT	qt_dispensar_ant_w
	FROM   	fa_entrega_medicacao_item x
	WHERE  	x.nr_seq_fa_entrega 	= nr_seq_anterior_w
	AND	x.cd_material		= cd_material_p
	AND	x.qt_gerado > x.qt_dispensar;
end if;


return	qt_dispensar_ant_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
